  nextMonth() {
    var day = new Date(this.state.month)
    if (day.getMonth() == 11) {
      day.setMonth(0)
      day.setFullYear(day.getFullYear() + 1)
    } else {
      day.setMonth(day.getMonth() + 1)
    }
    this.setState({
      month: new Date(day.toLocaleString())
    })
    return day
  }

  prevMonth() {
    var day = new Date(this.state.month)
    if (day.getMonth() == 0) {
      day.setMonth(11)
      day.setFullYear(day.getFullYear() - 1)
    } else {
      day.setMonth(day.getMonth() - 1)
    }
    this.setState({
      month: new Date(day.toLocaleString())
    })
    return day
  }

