import React, { Component } from 'react'
import moment from 'moment-timezone'
import { withRouter } from 'react-router-dom'
import { connect } from 'react-redux'
import IconButton from '@material-ui/core/IconButton'
import ArrowBack from '@material-ui/icons/ArrowBack'
import Grade from '@material-ui/icons/Grade'
import Share from '@material-ui/icons/Share'

// Material-UI
import { withStyles } from '@material-ui/core/styles'
import { MuiThemeProvider } from '@material-ui/core/styles'
import Grid from '@material-ui/core/Grid'
import Paper from '@material-ui/core/Paper'
import Typography from '@material-ui/core/Typography'
import TextField from '@material-ui/core/TextField'
import InputLabel from '@material-ui/core/InputLabel'
import MenuItem from '@material-ui/core/MenuItem'
import FormControl from '@material-ui/core/FormControl'
import Select from '@material-ui/core/Select'
import Input from '@material-ui/core/Input'
import Button from '@material-ui/core/Button'
import Checkbox from '@material-ui/core/Checkbox'
import ListItemText from '@material-ui/core/ListItemText'
import Dialog from '@material-ui/core/Dialog'
import DialogActions from '@material-ui/core/DialogActions'
import DialogContent from '@material-ui/core/DialogContent'
import DialogContentText from '@material-ui/core/DialogContentText'
import DialogTitle from '@material-ui/core/DialogTitle'
import Divider from '@material-ui/core/Divider'

import './Form.css'
// import Favorite from './Components/Favorite'

// Local
import Theme from '../Assets/Theme'
import Header from './Components/Header'
import HomeButton from './Components/HomeButton'
import CreateEvent from './CreateEvent'
import './Form.css'

const axios = require('axios')
const url = 'http://whatsroaring-api.herokuapp.com/'

const styles = theme => ({
  container: {
    display: 'flex',
    flexWrap: 'wrap',
  },
  textField: {
    marginLeft: theme.spacing.unit,
    marginRight: theme.spacing.unit,
  },
  dateTimePicker: {
    marginLeft: theme.spacing.unit,
    marginRight: theme.spacing.unit,
  },
  select: {
    marginLeft: theme.spacing.unit,
    marginRight: theme.spacing.unit,
  },
  dense: {
    marginTop: 19,
  },
})

class EditEvent extends CreateEvent {

  render() {
    const { classes } = this.props

    return (
      <div className='page'>
        <Header />
        <HomeButton />
        <MuiThemeProvider theme={Theme}>
          {this.renderDialogs()}
          <div className='main'>
            <Paper className='paper'>
              <div className='title'>
                <Typography variant="h3" color="primary">
                  Create Event
                </Typography>
              </div>
              <div className='form'>
                <FormControl>
                  <TextField
                    required
                    id='event-name'
                    label='Event Name'
                    className={classes.textField}
                    value={this.state.name}
                    onChange={this.handleChange('name')}
                    margin='normal'
                    variant='outlined'
                  />

                  <TextField
                    required
                    id='event-description'
                    label='Description'
                    className={classes.textField}
                    value={this.state.description}
                    onChange={this.handleChange('description')}
                    margin='normal'
                    variant='outlined'
                  />

                  <TextField
                    required
                    id='event-location'
                    label='Location'
                    className={classes.textField}
                    value={this.state.location}
                    onChange={this.handleChange('location')}
                    margin='normal'
                    variant='outlined'
                  />

                  <TextField
                    id='event-website'
                    label='Website'
                    className={classes.textField}
                    value={this.state.website}
                    onChange={this.handleChange('website')}
                    margin='normal'
                    variant='outlined'
                  />

                  <TextField
                    required
                    id="datetime-local"
                    label="Start Date & Time"
                    type="datetime-local"
                    className={classes.textField}
                    InputLabelProps={{
                      shrink: true,
                    }}
                    onChange={this.handleDateChange('startTime')}
                  />

                  <TextField
                    required
                    id="datetime-local"
                    label="End Date & Time"
                    type="datetime-local"
                    className={classes.textField}
                    InputLabelProps={{
                      shrink: true,
                    }}
                    onChange={this.handleDateChange('endTime')}
                  />
                </FormControl>
              </div>
              <div className='form'>
                <FormControl>
                  <InputLabel htmlFor="outlined-org-simple">
                    Organization *
                  </InputLabel>
                  <Select
                    value={this.state.org}
                    onChange={this.handleChange('org')}
                    className={classes.select}
                  >
                    {this.state.organizations.map(organization => (
                      <MenuItem key={organization} value={organization}>
                        {organization}
                      </MenuItem>
                    ))}
                  </Select>
                </FormControl>

                <FormControl>
                  <InputLabel htmlFor="select-multiple-checkbox">Category *</InputLabel>
                  <Select
                    multiple
                    value={this.state.category}
                    onChange={this.handleChange('category')}
                    input={<Input id="select-multiple-checkbox" />}
                    renderValue={selected => selected.join(', ')}
                    className={classes.select}
                    MenuProps={MenuProps}
                  >
                    {this.state.categories.map(category => (
                      <MenuItem key={category} value={category}>
                        <Checkbox checked={this.state.category.indexOf(category) > -1} color='primary'/>
                        <ListItemText primary={category} />
                      </MenuItem>
                    ))}
                  </Select>
                </FormControl>

                <FormControl>
                  <InputLabel htmlFor="outlined-isfree-simple">
                    Free? *
                  </InputLabel>
                  <Select
                    value={this.state.isFree}
                    onChange={this.handleChange('isFree')}
                    className={classes.select}
                  >
                    <MenuItem value={true}>Yes</MenuItem>
                    <MenuItem value={false}>No</MenuItem>
                  </Select>
                </FormControl>
              </div>
              <div className='button'>
                <Button variant="contained" color="primary" onClick={this.submitEvent} size="large">
                  Submit Event
                </Button>
              </div>
            </Paper>
          </div>
        </MuiThemeProvider>
      </div>
    )
  }
}

export default withStyles(styles)(withRouter(EditEvent))
